CREATE TABLE EVENTS (ID UUID NOT NULL DEFAULT RANDOM_UUID(), CREATED TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP(), EVENT_DATA VARCHAR NOT NULL);
CREATE TABLE CUSTOMERS (ID UUID NOT NULL DEFAULT RANDOM_UUID(), NAME VARCHAR NOT NULL, COUNTRY CHAR(2) NOT NULL, EMAIL VARCHAR NOT NULL);
CREATE TABLE CATEGORIES (ID UUID NOT NULL DEFAULT RANDOM_UUID(), NAME VARCHAR NOT NULL, PARENT_CATEGORY UUID, FOREIGN KEY (PARENT_CATEGORY) REFERENCES CATEGORIES(ID) ON DELETE CASCADE);
CREATE TABLE PRODUCTS (ID UUID NOT NULL DEFAULT RANDOM_UUID(), NAME VARCHAR NOT NULL, PRICE DOUBLE NOT NULL, FEATURES VARCHAR, NOTE VARCHAR, CATEGORY UUID, VISIBLE BOOLEAN NOT NULL DEFAULT TRUE, FOREIGN KEY(CATEGORY) REFERENCES CATEGORIES(ID) ON DELETE SET NULL);